import Navbar from "../../components/Navbar/Navbar"
import Header from "../../components/Header/Header"
import React, { Component} from 'react'
import axios from "axios"
import './Courses.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons"
import Dropdown from "react-dropdown"

export default class Courses extends Component {

  constructor()
  {
    super()
    this.state={
      res: [],
      mycourses: false,
      query_type: "Course Name"
    }
  }

  handleClick()
  {
    axios.get("http://localhost:5000/api/courses")
    .then(response => {
      console.log(response)
      this.setState({
        res: response.data
      })
    })
    console.log(this.state.res)
  }

  handleChange()
  {

  }

  render() {
    const options=["Course Code","Course Name"]
    return (
    <div>
        <Navbar/>
        <Header pos="Courses"/>
        <div className="Coursescontainer">
          <div className="Coursestitle">{this.state.mycourses ? "My Courses" : ""}</div>
          <div className="Coursesearchcontainer">
            <Dropdown options={options} onChange={(e)=>this.handleChange(e)} placeholder="select"/>
            <input placeholder="Course name" className="CourseSearchinput"/>
            <FontAwesomeIcon icon={faMagnifyingGlass} className="CourseSearch" onClick={()=>this.handleClick()}/>
          </div>
          {this.state.res && this.state.res.map(res => <div className="Courseitems">{res["name"]}</div>)}
        </div>
    </div>
    )
  }
}
