import Navbar from "../../components/Navbar/Navbar"
import Header from "../../components/Header/Header"
import React, { Component} from 'react'
import axios from "axios"
import './Courses.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons"
import Select from "react-select"

export default class Courses extends Component {

  constructor()
  {
    super()
    this.state={
      res: [],
      mycourses: false,
      query_type: ""
    }
  }

  handleClick()
  {
    axios.get("http://localhost:5000/api/courses")
    .then(response => {
      console.log(response)
      this.setState({
        res: response.data
      })
    })
    console.log(this.state.res)
  }

  handleChange(e)
  {
    const obj = e["value"]
    this.setState({
      query_type: obj
    })
  }

  render() {
    const options = [
      { value: 'Course name', label: 'Course name' },
      { value: 'Course code', label: 'Course code' }
    ]
    const customStyles = {
      control: (provided, state) => ({
        ...provided,
        background: '#fff',
        borderColor: '#9e9e9e',
        minHeight: '30px',
        height: '30px',
        boxShadow: state.isFocused ? null : null,
      }),
  
      valueContainer: (provided, state) => ({
        ...provided,
        height: '30px',
        padding: '0 6px'
      }),
  
      input: (provided, state) => ({
        ...provided,
        margin: '0px',
      }),
      indicatorSeparator: state => ({
        display: 'none',
      }),
      indicatorsContainer: (provided, state) => ({
        ...provided,
        height: '30px',
      }),
    };
    return (
    <div>
        <Navbar/>
        <Header pos="Courses"/>
        <div className="Coursescontainer">
          <div className="Coursestitle">{this.state.mycourses ? "My Courses" : "All Courses"}</div>
          <div className="Coursesearchcontainer">
            <Select styles={customStyles} options={options} onChange={(e)=>this.handleChange(e)}/>
            <input placeholder={this.state.query_type} className="CourseSearchinput"/>
            <FontAwesomeIcon icon={faMagnifyingGlass} className="CourseSearch" onClick={()=>this.handleClick()}/>
          </div>
          {this.state.res && this.state.res.map(res => <div className="Courseitems">{res["name"]}</div>)}
        </div>
    </div>
    )
  }
}
