import React, { Component } from 'react'
import Select from 'react-select'

export class DeptSelectWindow extends Component {
    
  render() {
    const options = [{
        "name": "CSE", "label":"CSE"
      }]
    return (
        <div className="DeptSelectWindow">
        <div className="DeptSelectQs">Department:</div>
        <Select options={options} onChange={(e)=>this.props.handleDeptSelChange(e)} placeholder="Select"/>
        <div className="DeptSelectOkbtn" onClick={()=>this.handleDeptClick()}>Ok</div>
      </div>
    )
  }
}

export default DeptSelectWindow
