            "14.30-15.55":"",
            "16.00-17.25":"",
            "17.30-17.55":"Snacks",
            "18.00-19.25":"",
            "19.30-21.00":"",
            "21.05-23.59":""
          },
          Thursday: {
            "00.00-8.55":"",
