import React, { Component } from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faCirclePlus, faMagnifyingGlass, faTriangleExclamation } from "@fortawesome/free-solid-svg-icons"

export class CourseRoadWindow extends Component {
  render() {
    return (
        <div ref = {this.CourseRef[index]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === sem) ? "active" : ""}`}>
        <div className="CourseRoadSemTitleContainer">
          <div onClick={()=>this.handleSemClick(sem)}>{sem} Semester</div>
          <FontAwesomeIcon onClick={()=>this.handlePlusClick(sem)} icon={faCirclePlus} style={{color: "#1290ff",}} />
        </div>
        <div className="CourseRoadSemCourseContainer">
          {this.state.Courses[index] && this.state.Courses[index].map(course => <Draggable onStart={()=>this.handleDragstart(sem)} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow" >{(course.msg.length !== 0) ? <div className="CourseRoadSemCourseItems active" onClick={()=>this.handleCourseClick(course)}> <FontAwesomeIcon icon={faTriangleExclamation} style={{color: "#1e90ff",}} /> <div>{course.name}</div></div>: <div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}><div>{course.name}</div></div>}</Draggable>)}
        </div>
        </div>
    )
  }
}

export default CourseRoadWindow
