          },
          Tuesday: {
            "00.00-8.55":"",
            "9.00-9.55":"",
            "10.00-10.55":"",
            "11.00-11.55":"",
            "12.00-12.55":"",
            "12.55-14.30":"Lunch",
            "14.30-15.55":"",
            "16.00-17.25":"",
            "17.30-17.55":"Snacks",
            "18.00-19.25":"",
