import Navbar from "../../components/Navbar/Navbar"
import Header from "../../components/Header/Header"
import React, { Component } from 'react'
import Calendar from "react-calendar"
import 'react-calendar/dist/Calendar.css';
import Tasks from "./Tasks";
import "./Home.css"
import Daily from "./Daily";
import Weekly from "./Weekly";
import moment from "moment/moment";
import Select from "react-select";

export default class Home extends Component {
  constructor()
  {
    super()
    this.state = {
      date : moment().format("MM/DD/YYYY"),
      task_name: "",
      task_date:""
    }
  }
  handleCalendar=(e,value)=>{
    //console.log(e.toLocaleDateString())
     this.setState({
      date: e.toLocaleDateString()
    })
    console.log(this.state.date)
  }
  handleChange(e,str)
  {
    (str === "task") ?
    this.setState({
      task_name: e.target.value
    }) : this.setState({
      task_date: e.target.value
    })
  }
  handleClick()
  {
    const handleTaskAdd = async () => {
      try {
        const headers = {
          'Authorization': `${sessionStorage.getItem('token')}`
        };
        const res = await axios.get(`http://localhost:5000/api/user/addTask`,{"name": [this.state.task_name], "date":[this.state]},{headers});
        this.setState({
          SearchRes: res.data
        })
      } catch (error) {
        console.log(error)
        }
      }
      handleCourseSearch()
  }

  render() {
    return (
    <div>
        <Navbar/>
        <Header/>
        <div className="Tasks">
          <Calendar onChange={this.handleCalendar} value={this.state.date}/>
          <Tasks date={this.state.date}/>
          <div className="AddTask">
            <div className="AddTaskItemsHead">Add Task</div>
            <input className="AddTaskItems" placeholder="Enter Task name" value={this.state.task_name} onChange={(e)=>this.handleChange(e,"task")}/>
            <input className="AddTaskItems" placeholder="Enter in MM/DD/YYYY format" type="text" pattern="\d{2}/\d{2}/\d{4}" value={this.state.task_date} onChange={(e)=>this.handleChange(e,"date")} />
            <div className="TaskOkbtn" onClick={()=>this.handleClick()}>Ok</div>
          </div>
        </div>
        <div className="Daily">
          <span className="DailyTitle"> Daily Tasks </span>
          <Daily/>
          <span className="WeeklyTitle"> Weekly Tasks </span>
          <Weekly/>
        </div>
    </div>
    )
  }
}
