//import React from "react"
import Navbar from "../../components/Navbar/Navbar"
import Header from "../../components/Header/Header"

import React, { Component } from 'react'
//import Draggable from "react-draggable"
import Select from "react-select"
import './CourseRoad.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faCirclePlus, faMagnifyingGlass, faTriangleExclamation } from "@fortawesome/free-solid-svg-icons"
import axios from "axios"
import Draggable from "react-draggable"

export default class CourseRoad extends Component {

  constructor(){
    super()
    this.CourseRef = [React.createRef(), React.createRef(), React.createRef(), React.createRef(),
    React.createRef(), React.createRef(), React.createRef(), React.createRef()]
    this.state={
      SelectedDep : "",
      currentDep : "",
      SelectedSem: "",
      SelectedPlusSem: "",
      SelectedCourse: "",
      DragprevSem: "",
      Input: "",
      SetLoading: true,
      Courses: [
      ["Intro to Programming"],
      ["Calculus II"],
      ["Database Management Systems I"],
      ["Operating Systems II"],
      ["Foundations Of Machine Learning"],
      ["Software Engineering"],
      ["Placements"],
      ["Timepass"],
      ]
    }
  }
  handleDeptClick()
  {
    this.setState({
      SetLoading: false
    })
    this.setState({
      SelectedDep : this.state.currentDep
    }, function(){
      var count = 0
      const handleCourseRoad = async (sem) => {
        try {
          const res = await axios.get(`http://localhost:5000/api/user/courseRoad?dept=${this.state.SelectedDep}&sem=${sem}`);
          console.log(sem, res.data); 
          count = count + 1
          console.log("Count is : ",count)
          if (count === 8)
          {
            this.setState({
              SetLoading: true
            })
          }
        } catch (error) {
          count = count+1
          console.log("Count is : ",count)
          console.error(sem, error)
          if (count === 8)
          {
            this.setState({
              SetLoading: true
            })
          }
          }
        }
      for(var sem = 1; sem <=8 ; sem++)
      {
        handleCourseRoad(sem)
      }
      console.log("Ok")
    })
  }
  handleDeptSelChange(e)
  {
    const obj = e["name"]
    this.setState({
      currentDep : obj
    })
  }
  handleSemClick(Sem)
  {
    (this.state.SelectedSem !== Sem) ?
    this.setState({
      SelectedSem : Sem
    }) : this.setState({SelectedSem : ""})
  }
  handlePlusClick(Sem)
  {
    (this.state.SelectedPlusSem !== Sem) ?
    this.setState({
      SelectedPlusSem : Sem
    }) : this.setState({SelectedPlusSem : ""})
  }
  handleInputChange(e)
  {
    this.setState({
      Input: e.target.value
    })
  }
  handleCourseAddOk()
  {
    const Stringtonum = {
      "First" : 1,"Second" : 2,"Third" : 3,"Fourth" : 4,"Fifth" : 5,"Sixth" : 6,"Seventh" : 7,"Eighth" : 8
    }
    const num = Stringtonum[this.state.SelectedPlusSem]-1
    var array = this.state.Courses[num]
    console.log(this.state.Courses[0])
    array = array.concat(this.state.Input)
    this.setState({
      Courses: [
        ...this.state.Courses.slice(0,num),
        array,
        ...this.state.Courses.slice(num+1)
      ]
    }, function(){
      console.log(this.state.Courses)
    })
    this.setState({
      Input: "",
      SelectedPlusSem: ""
    })
  }
  handleCourseClick(course)
  {
    (this.state.SelectedCourse !== course)?
    this.setState({
      SelectedCourse: course
    }) : this.setState({
      SelectedCourse: ""
    })
  }

  handleDragstart(Sem)
  {
    this.setState({
      DragprevSem : Sem
    })
  }
  
  handleDragend(e,data, course)
  {
    const Course_curr_y = e.clientY
    var currSem = 0
    for(var i=0; i<8; i++)
    {
      var top = this.CourseRef[i].current.getBoundingClientRect().top
      var height = this.CourseRef[i].current.getBoundingClientRect().height
      if(top < Course_curr_y && Course_curr_y < top + height)
      {
        currSem = i;
        break;
      }
    }
    const Stringtonum = {
      "First" : 1,"Second" : 2,"Third" : 3,"Fourth" : 4,"Fifth" : 5,"Sixth" : 6,"Seventh" : 7,"Eighth" : 8
    }
    const oldSem = Stringtonum[this.state.DragprevSem]-1
    if(oldSem === currSem)
    {
      return
    }
    const oldArray = this.state.Courses[oldSem].filter(Course => Course !== course)
    const newArray = this.state.Courses[currSem].concat(course)
    console.log(currSem)
    //this.state.Courses[oldSem].filter(Course => Course !== course),
    
    this.setState({
      Courses : [
        ...this.state.Courses.slice(0,oldSem),
        oldArray,
        ...this.state.Courses.slice(oldSem+1)
      ]
    }, function(){
      this.setState({
        Courses : [
          ...this.state.Courses.slice(0, currSem),
          newArray,
          ...this.state.Courses.slice(currSem+1)
        ]
      }, function(){
        console.log("new ",this.state.Courses)
      })
    })
  }

  render() {
    const options = [{
      "name": "CSE", "label":"CSE"
    }]
    const ElectiveTypeoptions = [
      { "name": "Core", "label":"Core"},
      { "name": "Departmental", "label":"Departmental"},
      { "name": "Free", "label":"Free"},
      { "name": "LA/CA", "label":"LA/CA"}
    ]
    return (
      <div>
        <Navbar/>
        <Header pos="CourseRoad"/>
        {this.state.SetLoading && (this.state.SelectedSem !== "") ? <div>
          <div className="SemCredInfoContainer">
            <div className="SemCredInfoItems">{this.state.SelectedSem} Semester</div>
            <div className="SemCredInfoItems">Total Cred: </div>
            <div className="SemCredInfoItems">Rem Dept Elec: </div>
            <div className="SemCredInfoItems">Rem Free Elec: </div>
            <div className="SemCredInfoItems">Rem LA/CA: </div>
          </div>
        </div> : null}
        {(this.state.SelectedDep === "") ? <div className="DeptSelectWindow">
          <div className="DeptSelectQs">Department:</div>
          <Select options={options} onChange={(e)=>this.handleDeptSelChange(e)} placeholder="Select"/>
          <div className="DeptSelectOkbtn" onClick={()=>this.handleDeptClick()}>Ok</div>
        </div> : null}
        {this.state.SetLoading && (this.state.SelectedDep !== "") ? <div className="CourseRoadWindow">
          <div ref = {this.CourseRef[0]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "First") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("First")}>First Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("First")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div id = "First" className="CourseRoadSemCourseContainer">
              {this.state.Courses[0] && this.state.Courses[0].map(course => <Draggable onStart={()=>this.handleDragstart("First")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow" ><div onClick={()=>this.handleCourseClick(course)} className="CourseRoadSemCourseItems">{course}</div></Draggable>)}
            </div>
          </div>
          <div ref = {this.CourseRef[1]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "Second") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("Second")}>Second Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("Second")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div id = "Second" className="CourseRoadSemCourseContainer">
            {this.state.Courses[1] && this.state.Courses[1].map(course => <Draggable onStart={()=>this.handleDragstart("Second")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow"><div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}><FontAwesomeIcon icon={faTriangleExclamation} style={{color: "#1e90ff",}} /><div>{course}</div></div></Draggable>)}
            </div>
          </div>
          <div ref = {this.CourseRef[2]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "Third") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("Third")}>Third Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("Third")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div className="CourseRoadSemCourseContainer">
            {this.state.Courses[2] && this.state.Courses[2].map(course => <Draggable onStart={()=>this.handleDragstart("Third")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow"><div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}>{course}</div></Draggable>)}
            </div>
          </div>
          <div ref = {this.CourseRef[3]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "Fourth") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("Fourth")}>Fourth Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("Fourth")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div className="CourseRoadSemCourseContainer">
            {this.state.Courses[3] && this.state.Courses[3].map(course => <Draggable onStart={()=>this.handleDragstart("Fourth")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow"><div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}>{course}</div></Draggable>)}
            </div>
          </div>
          <div ref = {this.CourseRef[4]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "Fifth") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("Fifth")}>Fifth Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("Fifth")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div className="CourseRoadSemCourseContainer">
            {this.state.Courses[4] && this.state.Courses[4].map(course => <Draggable onStart={()=>this.handleDragstart("Fifth")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow"><div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}>{course}</div></Draggable>)}
            </div>
          </div>
          <div ref = {this.CourseRef[5]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "Sixth") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("Sixth")}>Sixth Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("Sixth")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div className="CourseRoadSemCourseContainer">
            {this.state.Courses[5] && this.state.Courses[5].map(course => <Draggable onStart={()=>this.handleDragstart("Sixth")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow"><div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}>{course}</div></Draggable>)}
            </div>
          </div>
          <div ref = {this.CourseRef[6]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "Seventh") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("Seventh")}>Seventh Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("Seventh")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div className="CourseRoadSemCourseContainer">
            {this.state.Courses[6] && this.state.Courses[6].map(course => <Draggable onStart={()=>this.handleDragstart("Seventh")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow"><div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}>{course}</div></Draggable>)}
            </div>
          </div>
          <div ref = {this.CourseRef[7]} className={`CourseRoadSemContainer ${(this.state.SelectedSem === "Eighth") ? "active" : ""}`}>
            <div className="CourseRoadSemTitleContainer">
              <div onClick={()=>this.handleSemClick("Eighth")}>Eighth Semester</div>
              <FontAwesomeIcon onClick={()=>this.handlePlusClick("Eighth")} icon={faCirclePlus} style={{color: "#1290ff",}} />
            </div>
            <div className="CourseRoadSemCourseContainer">
            {this.state.Courses[7] && this.state.Courses[7].map(course => <Draggable onStart={()=>this.handleDragstart("Eighth")} onStop={(e, data)=>this.handleDragend(e, data, course)} bounds=".CourseRoadWindow"><div className="CourseRoadSemCourseItems" onClick={()=>this.handleCourseClick(course)}>{course}</div></Draggable>)}
            </div>
          </div>
        </div> : null}
        {this.state.SetLoading && (this.state.SelectedPlusSem !== "") ? <div className="CourseRoadOptsContainer">
          <div className="CourseRoadAddOptsContainer">
            <div className="CourseRoadOptsItems">Elective Type:</div>
            <Select options={ElectiveTypeoptions} placeholder="Select"/>
            <div className="CourseRoadOptsItems">
              <input onChange={(e)=>this.handleInputChange(e)}/>
              <FontAwesomeIcon icon={faMagnifyingGlass} className="CourseRoadSearch" />
            </div>
            <div className="DeptSelectOkbtn" onClick={()=>this.handleCourseAddOk()}>Ok</div>
          </div>
        </div> : null}
        {this.state.SetLoading && (this.state.SelectedCourse !== "") ? <div className="CourseInfoContainer">
          <div className="CourseInfoTitle">Course Info:</div>
          <div className="CourseInfoItemsContainer"><div className="CourseInfoItems1">Name :</div><div className="CourseInfoItems2">{this.state.SelectedCourse}</div></div>
          <div className="CourseInfoItemsContainer"><div className="CourseInfoItems1">Course Code:</div><div className="CourseInfoItems2">CS0000</div></div>
          <div className="CourseInfoItemsContainer"><div className="CourseInfoItems1">Timetable Slot:</div><div className="CourseInfoItems2">X</div></div>
          <div className="CourseInfoItemsContainer"><div className="CourseInfoItems1">Instructor:</div><div className="CourseInfoItems2">Virat Kohli</div></div>
          <div className="CourseInfoItemsContainer"><div className="CourseInfoItems1">Description:</div><div className="CourseInfoItems2">Worst Course</div></div>
        </div> : null}
      </div>
    )
  }
}
