//import React from "react"
import Navbar from "../../components/Navbar/Navbar"
import Header from "../../components/Header/Header"

import React, { Component } from 'react'
//import Draggable from "react-draggable"
import Select from "react-select"
import './CourseRoad.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faCirclePlus, faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons"
import Draggable from "react-draggable"

export default class CourseRoad extends Component {

  constructor(){
    super()
    this.FirstRef = React.createRef();
    this.SecondRef = React.createRef();
    this.ThirdRef = React.createRef();
    this.FourthRef = React.createRef();
    this.FifthRef = React.createRef();
    this.state={
      SelectedDep : "",
      currentDep : "",
      SelectedSem: "",
      SelectedPlusSem: "",
      SelectedCourse: "",
      Input: "",
      First: ["Software Engineering"],
      Second: ["Software Engineering"],
      Third: ["Software Engineering"],
      Fourth: ["Software Engineering"],
      Fifth: ["Software Engineering"],
      Sixth: ["Software Engineering"],
      Seventh: ["Software Engineering"],
      Eighth: ["Software Engineering"],
    }
  }
  handleDeptClick()
  {
    this.setState({
      SelectedDep : this.state.currentDep
    })
  }
  handleDeptSelChange(e)
  {
    const obj = e["name"]
    this.setState({
      currentDep : obj
    })
  }
  handleSemClick(Sem)
  {
    (this.state.SelectedSem !== Sem) ?
    this.setState({
      SelectedSem : Sem
    }) : this.setState({SelectedSem : ""})
  }
  handlePlusClick(Sem)
  {
    (this.state.SelectedPlusSem !== Sem) ?
    this.setState({
      SelectedPlusSem : Sem
    }) : this.setState({SelectedPlusSem : ""})
  }
  handleInputChange(e)
  {
    this.setState({
      Input: e.target.value
    })
  }
  handleCourseAddOk()
  {
    switch(this.state.SelectedPlusSem) {
      case "First":
        this.setState(prevState =>({
          First: [...prevState.First, this.state.Input]
        })); break;
      case "Second":
        this.setState(prevState =>({
          Second: [...prevState.Second, this.state.Input]
        })); break;
      case "Third":
        this.setState(prevState =>({
          Third: [...prevState.Third, this.state.Input]
        })); break;
      case "Fourth":
        this.setState(prevState =>({
          Fourth: [...prevState.Fourth, this.state.Input]
        })); break;
      case "Fifth":
        this.setState(prevState =>({
          Fifth: [...prevState.Fifth, this.state.Input]
        })); break;
      case "Sixth":
        this.setState(prevState =>({
          Sixth: [...prevState.Sixth, this.state.Input]
        })); break;
      case "Seventh":
        this.setState(prevState =>({
          Seventh: [...prevState.Seventh, this.state.Input]
        })); break;
      case "Eighth":
        this.setState(prevState =>({
          Eighth: [...prevState.Eighth, this.state.Input]
        })); break;
      default:
        console.log("Error!! ", this.state.SelectedPlusSem)
        break;
    }
    this.setState({
      Input: "",
      SelectedPlusSem: ""
    })
  }
  handleCourseClick(course)
  {
    (this.state.SelectedCourse !== course)?
    this.setState({
      SelectedCourse: course
    }) : this.setState({
      SelectedCourse: ""
    })
    console.log(this.state.SelectedCourse)
  }
  
  handleDragend(e,data,Sem)
  {
    //console.log(e)
    //console.log(data)
    const numtoString = {
       "First" : 1,"Second" : 2,"Third" : 3,"Fourth" : 4,"Fifth" : 5,"Sixth" : 6,"Seventh" : 7,"Eighth" : 8
    }
    const heights = {
      1 : this.FirstRef.current.getBoundingClientRect().height,
      2 : this.SecondRef.current.getBoundingClientRect().height,
      3 : this.ThirdRef.current.getBoundingClientRect().height,
      4 : this.FourthRef.current.getBoundingClientRect().height,
      5 : this.FifthRef.current.getBoundingClientRect().height,
      6 : this.SixthRef.current.getBoundingClientRect().height,
      7 : this.SeventhRef.current.getBoundingClientRect().height,
      8 : this.EighthRef.current.getBoundingClientRect().height,
    }

    const curr_height_offset = data.y
    const start = numtoString[Sem]
    const end = (curr_height_offset < 0) ? 1 : 8 
