import React, { Component } from 'react'
import './Weekly.css'
import moment from 'moment'

export default class Weekly extends Component {
  constructor(){
    super()
    this.state={
      week: []
    }
  }
  componentDidMount()
  {
    const newweek = []
    for(var i=0; i<7;i++)
    {
      newweek.concat(moment().startOf('week').add(i, 'days').format("MM/DD/YYYY"))
    }
    
    this.setState({
      week: newweek
    })
  }
  render() {
    return (
      <div className='Weeklycontainer'>
        {this.state.week && this.state.week.map((day, index) => (
          this.all_tasks[day] && this.all_tasks[day].map((course, id) => <div className='Weeklyitems'>{course}</div>)
        ))}
      </div>
    )
  }
}
